sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		var rightNowDate = new GlideDateTime();
		var inputDate = new GlideDateTime(strDate);

		if(inputDate.before(rightNowDate)){
			return true;
		}
		else return false;
	},
	isDateToday: function(strDate){
		var rightNowDateTime = new GlideDateTime();
		var inputDateTime = new GlideDateTime(strDate);

		var rightNowDate = rightNowDateTime.getLocalDate();
		var inputDate = inputDateTime.getLocalDate();

		if(rightNowDate == inputDate){
			return true;
		}
		else return false;
	},
    type: 'NeedItUtils'
};
